foreign_songs = [] #list for foreign language songs
unclear_names = [] #list for unclear names

for song_name in playlist_items:

    if song_name.isdigit() or len(song_name)<4: #checks if contains a number or too short
        unclear_names.append(song_name)
        continue

    lan_of_song = str(detect(str(song_name))) #detects the language of song with the help of lang-detect library
    if lan_of_song != "tr":
        foreign_songs.append(song_name)

print(foreign_songs)
print(len(foreign_songs))
